mapper.select:
SELECT 
FROM xxl_mq_message_archive AS t
WHERE t.`id` = #{id}

SELECT 
FROM xxl_mq_message_archive AS t
WHERE `topic` = #{topic}

        AND t.status = #{status}


        AND t.effect_time  >=  #{effectTimeStart}


        AND t.effect_time  <=  #{effectTimeEnd}

ORDER BY t.`effect_time` DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1)
FROM xxl_mq_message_archive AS t
WHERE `topic` = #{topic}

        AND t.status = #{status}


        AND t.effect_time  >=  #{effectTimeStart}


        AND t.effect_time  <=  #{effectTimeEnd}

mapper.delete:
DELETE FROM xxl_mq_message_archive
WHERE `id` in (
    SELECT `id` FROM(
        SELECT `id`
        FROM xxl_mq_message_archive
        WHERE `topic` = #{topic}

                AND `effect_time`  <  #{effectTimeFrom}

        ORDER BY `id` ASC
        LIMIT #{pageSize}
    ) as t
)

mapper.select:
SELECT count(1)
FROM xxl_mq_message_archive AS t
WHERE t.`topic` = #{topic}
AND t.`status` IN

    #{item}

AND t.`add_time`  >=  #{dateFrom}
AND t.`add_time`  <=  #{dateTo}
