mapper.select:
SELECT 
FROM xxl_mq_message AS t
WHERE t.`id` = #{id}

SELECT 
FROM xxl_mq_message AS t
WHERE t.`topic` = #{topic}

        AND t.status = #{status}


        AND t.effect_time  >=  #{effectTimeStart}


        AND t.effect_time  <=  #{effectTimeEnd}

ORDER BY t.`id` ASC
LIMIT #{offset}, #{pagesize}

SELECT count(1)
FROM xxl_mq_message AS t
WHERE t.`topic` = #{topic}

        AND t.status = #{status}


        AND t.effect_time  >=  #{effectTimeStart}


        AND t.effect_time  <=  #{effectTimeEnd}

SELECT 
FROM xxl_mq_message AS t
WHERE t.`topic` = #{topic}
    AND t.`status` in

        #{item}

    AND t.`retry_count_remain` = 0
ORDER BY t.`id` ASC
LIMIT #{pageSize}

mapper.update:
UPDATE xxl_mq_message SET `status` =
when #{messageItem.id} then #{messageItem.status}
, `consume_log` =
when #{messageItem.id} then CONCAT(`consume_log`, #{messageItem.consumeLog})
WHERE `id` IN
#{messageItem.id}

mapper.select:
WITH PullTopicData AS ( SELECT `id`, `topic`, `data`, ROW_NUMBER() OVER (PARTITION BY `topic` ORDER BY `id`) as rn FROM xxl_mq_message WHERE `topic` IN
#{item}
AND `status` = #{status} AND `partition_id` BETWEEN #{partitionIdFrom} AND #{partitionIdTo} AND `effect_time` <= NOW() ORDER BY `id` asc LIMIT #{pagesize} ) SELECT `id`, `topic`, `data` FROM PullTopicData WHERE rn <= #{pagesize}

mapper.update:
UPDATE xxl_mq_message SET `status` = #{statuTo}, `consume_instance_uuid` = #{instanceUuid}, `update_time` = NOW() WHERE `id` IN
#{item}
AND status = #{statuFrom}

mapper.select:
SELECT 
FROM xxl_mq_message AS t
WHERE t.`topic` = #{topic}
AND t.`id` IN

        #{item}

    AND t.`status` = #{status}
    AND t.`consume_instance_uuid` = #{instanceUuid}

SELECT FROM xxl_mq_message AS t WHERE t.`id` IN
#{item}
AND t.`status` IN
#{item}
AND t.`retry_count_remain` > 0

SELECT 
FROM xxl_mq_message AS t
WHERE t.`status` IN

            #{item}

    AND t.`retry_count_remain`  >  0
LIMIT #{pagesize}

mapper.update:
UPDATE xxl_mq_message AS t SET t.`status` = #{newStatus}, t.`effect_time` =
when #{messageItem.id} then #{messageItem.effectTime}
, t.`retry_count_remain` = retry_count_remain - 1, t.`consume_log` = CONCAT( IFNULL(t.`consume_log`, '') , #{consumeLog} ), t.`update_time` = NOW() WHERE t.`id` IN
#{messageItem.id}
AND t.`status` IN
#{item}
AND t.`retry_count_remain` > 0

UPDATE xxl_mq_message
SET
`status` = #{failStatus},
`update_time` = NOW()
WHERE `status` = #{runningStatus}
    AND update_time  <  #{stuckTime}
LIMIT #{pagesize}

mapper.select:
SELECT count(1)
FROM xxl_mq_message AS t
WHERE t.`topic` = #{topic}
    AND t.`add_time`  >=  #{dateFrom}
    AND t.`add_time`  <=  #{dateTo}
