mapper.select:
SELECT 
FROM XXL_MQ_MESSAGE AS t


		AND t.topic = #{topic}


		AND t.status = #{status}


		AND t.addTime  >=  #{addTimeStart}


		AND t.addTime  <=  #{addTimeEnd}


ORDER BY t.id DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1)
FROM XXL_MQ_MESSAGE AS t


		AND t.topic = #{topic}


		AND t.status = #{status}


		AND t.addTime  >=  #{addTimeStart}


		AND t.addTime  <=  #{addTimeEnd}

SELECT 
FROM XXL_MQ_MESSAGE AS t
WHERE 	t.topic = #{topic}
	AND t.group = #{group}
	AND t.status = #{newStatus}
	AND t.effectTime  <  NOW()

		AND (
			(
				t.shardingId  =  0
				AND
				MOD(t.id, #{consumerTotal}) = #{consumerRank}
			)
			OR
			(
				t.shardingId  >  0
				AND
				MOD(t.shardingId, #{consumerTotal}) = #{consumerRank}
			)
		)

ORDER BY t.id ASC
LIMIT #{pagesize}

mapper.update:
UPDATE XXL_MQ_MESSAGE SET status =
when #{messageItem.id} then #{messageItem.status}
, log =
when #{messageItem.id} then CONCAT(log, #{messageItem.log})
WHERE id IN
#{messageItem.id}

mapper.delete:
DELETE FROM XXL_MQ_MESSAGE
WHERE status =  #{successStatus}
	AND addTime  <  DATE_SUB(NOW(), Interval #{logretentiondays} DAY)

        delete from XXL_MQ_MESSAGE
        where 1 = 1

				AND topic = #{topic}



                AND `status` = #{status}


                AND addTime  <=  DATE_SUB(NOW(), Interval 1 MONTH )


                AND addTime  <=  DATE_SUB(NOW(), Interval 3 MONTH )


                AND addTime  <=  DATE_SUB(NOW(), Interval 6 MONTH )


                AND addTime  <=  DATE_SUB(NOW(), Interval 12 MONTH )


                AND id NOT in(
                    SELECT id FROM(
                        SELECT t.id FROM XXL_MQ_MESSAGE AS t
                        where t.topic = #{topic}

                                AND t.status = #{status}

                        ORDER BY t.id desc

                            LIMIT 1000


                            LIMIT 10000


                            LIMIT 100000

                    ) t1
                )
