mapper.select:
SELECT 
FROM XXL_MQ_MESSAGE AS t


		AND t.topic like CONCAT(CONCAT('%', #{topic}), '%')


		AND t.status = #{status}


ORDER BY t.id DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1) FROM XXL_MQ_MESSAGE AS t
AND t.topic like CONCAT(CONCAT('%', #{topic}), '%')
AND t.status = #{status}

SELECT 
FROM XXL_MQ_MESSAGE AS t
WHERE 	t.topic = #{topic}
	AND t.group = #{group}
	AND t.status = #{newStatus}
	AND t.effectTime  <  NOW()

		AND (
			(
				t.shardingId  =  0
				AND
				MOD(t.id, #{consumerTotal}) = #{consumerRank}
			)
			OR
			(
				t.shardingId  >  0
				AND
				MOD(t.shardingId, #{consumerTotal}) = #{consumerRank}
			)
		)

ORDER BY t.id DESC
LIMIT #{pagesize}

mapper.update:
UPDATE XXL_MQ_MESSAGE SET status =
when #{messageItem.id} then #{messageItem.status}
, log =
when #{messageItem.id} then CONCAT(log, #{messageItem.log})
WHERE id IN
#{messageItem.id}

mapper.delete:
DELETE FROM XXL_MQ_MESSAGE
WHERE status =  #{successStatus}
	AND addTime  <  DATE_SUB(NOW(), Interval #{logretentiondays} DAY)
