mapper.select:
SELECT 
FROM XXL_MQ_MESSAGE AS t


		AND t.name = #{name}


		AND t.status = #{status}


ORDER BY t.add_time DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1)
FROM XXL_MQ_MESSAGE AS t


		AND t.name = #{name}


		AND t.status = #{status}


ORDER BY t.add_time DESC

mapper.update:
UPDATE XXL_MQ_MESSAGE
SET	update_time = NOW(),
	status = #{status},
	msg = msg + #{msg}
WHERE id = #{id}

mapper.select:
SELECT 
FROM XXL_MQ_MESSAGE AS t
WHERE t.name = #{name}
	AND t.status = #{status}

		AND MOD(t.id, #{consumerTotal}) = #{consumerRank}

ORDER BY t.add_time DESC
LIMIT #{pagesize}
