mapper.select:
SELECT 
FROM XXL_MQ_MESSAGE AS t


		AND t.name = #{name}


		AND t.status = #{status}


ORDER BY t.update_time DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1)
FROM XXL_MQ_MESSAGE AS t


		AND t.name = #{name}


		AND t.status = #{status}


ORDER BY t.update_time DESC

SELECT 
FROM XXL_MQ_MESSAGE AS t
WHERE t.id = #{id}

mapper.update:
UPDATE XXL_MQ_MESSAGE
SET	update_time = NOW(),
	status = #{status},
	msg = msg + #{msg}
WHERE id = #{id}
