mapper.select:
SELECT 
FROM XXL_MQ_MESSAGE AS t


		AND t.name like CONCAT(CONCAT('%', #{name}), '%')


		AND t.status = #{status}


ORDER BY t.add_time DESC
LIMIT #{offset}, #{pagesize}

SELECT 
FROM XXL_MQ_MESSAGE AS t
WHERE t.name = #{name}
AND t.status = #{newStatus}
AND t.delay_time  <  NOW()

	AND MOD(t.id, #{consumerTotal}) = #{consumerRank}

ORDER BY t.add_time DESC
LIMIT #{pagesize}
